import globalModule from '../components/styles.css'

export const getModuleClasses = (localModule, classNames) => {
  const styles = {
    ...localModule,
    ...globalModule
  }
  return classNames
    .replace(/^\s+|\s+$/g, '')
    .split(/\s+/)
    .map((cl) => styles[cl.trim()])
    .join(' ')
    .trim()
}

export const callCallback = (callback, data) => {
  if (callback && typeof callback === 'function') {
    callback(data)
  }
}

export const pickKeys = (obj, keys) => {
  const newObj = {}
  const keysLength = keys.length
  for (let i = 0; i < keysLength; i++) {
    if (Object.prototype.hasOwnProperty.call(obj, keys[i])) {
      newObj[keys[i]] = obj[keys[i]]
    }
  }
  return newObj
}

export const uid = () => {
  return `_${(((1 + Math.random()) * 0x10000) | 0).toString(8).substring(1)}_`
}

export const clearAllTimeout = () => {
  let timeoutId = setTimeout(() => {})
  while (timeoutId--) {
    clearTimeout(timeoutId)
  }
}
const defaultRootCSSVariables = {
  '--light-bg': '#E4EBF5',
  '--light-bg-dark-shadow': '#bec8e4',
  '--light-bg-light-shadow': '#ffffff',
  '--dark-bg': '#444444',
  '--dark-bg-dark-shadow': '#363636',
  '--dark-bg-light-shadow': '#525252',
  '--white': '#ffffff',
  '--black': '#000000',
  '--primary': '#f71b94',
  '--primary-dark': '#aa0660',
  '--primary-light': '#fa7ac0',
  '--g-text-color-light': 'rgba(0, 0, 0, 0.87)',
  '--g-text-color-disabled-light': 'rgba(0, 0, 0, 0.38)',
  '--g-text-color-secondary-light': 'rgba(0, 0, 0, 0.60)',
  '--g-text-color-dark': 'rgba(255, 255, 255, 0.87)',
  '--g-text-color-disabled-dark': 'rgba(255, 255, 255, 0.38)',
  '--g-text-color-secondary-dark': 'rgba(255, 255, 255, 0.60)',
  '--g-bg-color-disabled-light': '#dee5e8',
  '--g-bg-color-disabled-dark': '#727272'
}

/**
 * Function to change a given css variable
 * @param {*} element
 * @param {*} variable
 * @param {*} value
 */
export const setCSSVariable = (element, variable, value) => {
  if (element && value) {
    element.style.setProperty(variable, String(value))
  }
}

/**
 * Getter function to fetch all default css variables object
 */
export const getDefaultThemeVariables = () => {
  return defaultRootCSSVariables
}

/**
 * Function to change root css variables
 * @param {*} themeObject
 */
export const overrideThemeVariables = (themeObject) => {
  const root = document.querySelector(':root')
  const themeVariables = Object.keys(themeObject)
  if (root && themeObject) {
    themeVariables.forEach((themeVar) => {
      const varValue = themeObject[themeVar]
      if (varValue && themeVar.startsWith('--')) {
        setCSSVariable(root, themeVar, varValue)
      }
    })
  }
}

/**
 *
 * @param {*} path
 * @param {*} element
 */
export const detectElementInDOM = (path = [], element = 'null') => {
  return path
    .map((elem) => elem.nodeName)
    .join('-')
    .toLowerCase()
    .includes(element.toLowerCase())
}

/**
 *
 * @param {*} event : DOM click event
 * @param {*} node : DOM node to detect click inside of
 */

export const findClickInside = (event, node) => {
  let currentNode = event.target
  try {
    do {
      if (currentNode === node) {
        // click is inside
        return true
      }
      currentNode = currentNode.parentNode
    } while (currentNode)
    // click is outside
    return false
  } catch (err) {
    throw new Error(err)
  }
}
import {
  Children,
  cloneElement
} from 'react'

/**
 * Check if component is valid in library
 * @param {*} component
 */
export const isMyReactComponent = (component) => {
  let componentName = ''
  if (component && component.type && typeof component.type !== 'string') {
    componentName = component.type.displayName
  }
  return componentName && componentName.includes('Nu')
}

/**
 * Pass down given prop to all given children
 * @param {*} children
 * @param {*} props
 * @param {*} propName
 */
export const passDownProp = (children, props, propName) => {
  return Children.map(children, (child) => {
    if (isMyReactComponent(child)) {
      const newProps = {
        ...child.props
      }
      if (typeof propName === 'string') {
        newProps[propName] = child.props[propName] === undefined ? props[propName] : child.props[propName]
      } else if (Array.isArray(propName)) {
        propName.forEach(prop => {
          newProps[prop] = child.props[prop] === undefined ? props[prop] : child.props[prop]
        })
      }
      return cloneElement(child, newProps)
    } else {
      return child
    }
  })
}
import React from 'react'

const withImage = (WrappedComponent, config = {}) => {
  return class withImage extends React.Component {
    image

    static displayName = `WithImage(${
      WrappedComponent.displayName || WrappedComponent.name || 'Component'
    })`

    constructor(props) {
      super(props)
      this.state = {
        loaded: false,
        height: 0,
        width: 0
      }
    }

    setLoaded() {
      const { src } = this.props
      if (!src) {
        return null
      }
      // eslint-disable-next-line no-undef
      this.image = new Image()
      this.image.src = src
      this.image.onload = () => {
        const { width, height } = this.image
        this.setState((state) => ({ ...state, width, height, loaded: true }))
      }
      this.image.onerror = () => {
        this.setState((state) => ({
          ...state,
          width: 0,
          height: 0,
          loaded: false
        }))
      }
    }

    componentWillMount() {
      this.setLoaded()
    }

    componentDidUpdate({ src }) {
      if (this.props.src !== src) {
        this.setLoaded()
      }
    }

    componentWillUnmount() {
      if (this.image) {
        this.image.onerror = null
        this.image.onload = null
      }
    }

    render() {
      const { loaded, width, height } = this.state
      return (
        <WrappedComponent
          {...this.props}
          loaded={loaded}
          imageWidth={width}
          imageHeight={height}
        />
      )
    }
  }
}

export default withImage
import React from 'react'
import { findDOMNode } from 'react-dom'

const withResize = (WrappedComponent, config = {}) => {
  return class withResize extends React.Component {
    static displayName = `WithResize(${
      WrappedComponent.displayName || WrappedComponent.name || 'Component'
    })`

    constructor(props) {
      super(props)
      this.state = {
        height: null,
        width: null
      }
      this.node = null
      this.resizeObserver = null
      this.updateDimensions = this.updateDimensions.bind(this)
    }

    componentDidMount() {
