    return (
      <TransitionWrapper
        onUpdate={this.updateStatus}
        timeout={{ exit: duration }}
        {...otherProps}
      >
        {cloneElement(child, {
          style: {
            opacity: 0,
            transformOrigin: origin,
            ...this.transitionStyles[status],
