    size: 'medium',
    ...DEFAULT_PROPS
  }

  static propTypes = BUTTON_PROP_TYPES

  getValidSize(size) {
    return SIZES.find((s) => s === size) || 'medium'
  }

  getClasses(classType) {
    const {
      type,
      dark,
      size,
      text,
      block,
      active,
      noPress,
      rounded,
      disabled,
      bordered,
      outlined,
      depressed
    } = this.props
    switch (classType) {
      case 'container':
        return getModuleClasses(
          styles,
          `
            nu-button
            cursor-pointer
            nu-button--${type}
            ${text ? 'nu-button--text' : ''}
            ${block ? 'nu-button--block' : ''}
            ${active ? 'nu-button--active' : ''}
            nu-button--${this.getValidSize(size)}
