      value: value || '',
      id: `${id || uid()}`,
      count: (value || '').length
    }
  }

  get input() {
    const {
      tag,
      type,
      name,
      width,
      height,
      readonly,
      autofocus,
      inputStyles,
      placeholder
    } = this.props
    const { id, value } = this.state
    const className = `${this.getClasses('text-field')} ${
      tag === 'textarea' ? this.getClasses('text-area') : ''
    }`
    const events = pickKeys(this.props, ['onInput', 'onKeyUp', 'onKeyDown'])

    const inputProps = {
      id: id,
      className,
      type: type,
      name: name,
      value: value,
      readOnly: readonly,
      autoFocus: autofocus,
      placeholder: placeholder,
      disabled: this.isDisabled,
      onBlur: (e) => this.handleBlur(e),
      onFocus: (e) => this.handleFocus(e),
      onChange: (e) => this.handleChange(e),
      tabIndex: this.isDisabled ? -1 : undefined,
      style: {
        width: `${width}px`,
        height: `${height}px`,
        minHeight: `${height}px`,
        ...inputStyles
      },
      ...events
    }
    return createElement(tag, inputProps)
  }

  get canShowLabel() {
    const { value } = this.state
    const { placeholder } = this.props
    return !placeholder && !value
  }

  get isDisabled() {
    const { loading, disabled } = this.props
    return loading ? true : disabled
  }

  validate(value) {
    let valid = true
    let errorMessage = 'Invalid'
    const { rules = [], counter } = this.props
    const ruleLength = rules.length
    for (let i = 0; i < ruleLength; i++) {
      const isValid = rules[i](value)
      if (isValid !== true) {
        errorMessage = isValid
        valid = false
        break
      }
    }
    if (counter !== undefined && value.length > counter) {
      valid = false
      errorMessage = `Max ${counter} characters`
    }
    this.setState({ valid })
    this.setState({ errorMessage })
  }

  handleChange(event) {
    const { id, valid } = this.state
    const { onChange, hideExtra, uncontrolled } = this.props
    const value = event.target.value
    const count = value.length

    if (!hideExtra) {
      this.validate(value)
    }

    if (!uncontrolled) {
      this.setState({ value })
    }
    this.setState({ count })
    callCallback(onChange, { event, id, value, valid })
  }

  handleFocus(event) {
    const { id } = this.state
    const { onFocus } = this.props
    this.setState({ focused: true })
    callCallback(onFocus, { event, id })
  }

  handleBlur(event) {
    const { id } = this.state
    const { onBlur } = this.props
    this.setState({ focused: false })
    callCallback(onBlur, { event, id })
  }

  getClasses(classType, flag) {
    const {
      dark,
      dense,
      rounded,
      readonly,
      outlined,
      bordered,
      disabled
    } = this.props
    if (classType === 'container') {
      return getModuleClasses(
        styles,
        `
            nu-text-field-container 
            ${dense ? 'nu-text-field-container--dense' : ''}
            ${rounded ? 'nu-text-field-container--rounded' : ''}
            ${disabled ? 'nu-text-field-container--disabled' : ''}
            `
      )
    } else if (classType === 'text-field') {
      return getModuleClasses(
        styles,
        `
            nu-text-field
            nu-text-field--${dark ? 'dark' : 'light'}
            ${rounded ? 'nu-text-field--rounded' : ''}
            ${outlined ? 'nu-text-field--outlined' : ''}
            ${bordered ? 'nu-text-field--bordered' : ''}
            ${readonly ? 'nu-text-field--readonly' : ''}
            ${this.isDisabled ? 'nu-text-field--disabled' : ''}
            `
      )
    } else if (classType === 'error') {
      return getModuleClasses(
        styles,
        `nu-text-field-${flag ? 'hint' : 'error'}`
      )
    } else {
      return getModuleClasses(styles, `nu-text-field-${classType}`)
    }
  }

  componentWillUpdate(props, state) {
    const { value, uncontrolled } = props
    if (state.value !== value && uncontrolled) {
      this.setState({ value })
    }
  }

  render() {
    const {
      dark,
      hint,
      label,
      style,
      append,
      prepend,
      counter,
      loading,
      disabled,
      className,
      hideExtra
    } = this.props
    const { id, valid, count, errorMessage } = this.state
    return (
      <div
        style={style}
        className={`${this.getClasses('wrapper')} ${className}`}
      >
        {prepend ? (
          <div className={`${this.getClasses('prepend')}`}>{prepend}</div>
        ) : null}
        <div className={`${this.getClasses('container')}`}>
          {this.canShowLabel ? (
            <label htmlFor={id} className={`${this.getClasses('label')}`}>
              <Subtitle1
                secondary
                dark={dark}
                component='div'
                disabled={disabled}
              >
                {label}
              </Subtitle1>
            </label>
          ) : null}
          {loading ? (
            <ProgressLinear
              fillHeight
              height={2}
              dark={dark}
              indeterminate
              className={`${this.getClasses('loading')}`}
            />
          ) : null}
          {this.input}
          {hideExtra && !counter ? null : (
            <div className={`${this.getClasses('caption-wrapper', valid)}`}>
              {hideExtra ? (
                hint
              ) : (
                <Caption
                  secondary
                  dark={dark}
                  component='div'
                  className={`${this.getClasses('error', valid)}`}
                >
                  {valid ? hint : errorMessage}
                </Caption>
              )}
              {counter ? (
                <Caption
                  secondary
                  dark={dark}
                  component='div'
                  className={`${this.getClasses('counter')}`}
                >
                  {count}/{counter}
                </Caption>
              ) : null}
            </div>
          )}
        </div>
        {append ? (
          <div className={`${this.getClasses('append')}`}>{append}</div>
        ) : null}
      </div>
    )
  }
}

export default TextField
import React from 'react'

import { TextField } from '../../index'
import { callCallback } from '../../util'

class TextArea extends React.Component {
  static displayName = 'NuTextarea'

  constructor(props) {
    super(props)
    const { dense, height } = props
    const defaultHeight = dense ? 32 : 40
    this.minHeight = Math.max(height || 0, defaultHeight)
  }

  onInput(e, prop) {
    if (!this.props.autoExpand) return
    const textarea = e.target

    const outerHeight = parseInt(window.getComputedStyle(textarea).height, 10)
    const diff = outerHeight - textarea.clientHeight

    textarea.style.height = 0
    textarea.style.height = `${Math.max(
      this.minHeight,
      textarea.scrollHeight + diff
    )}px`

    callCallback(this.props[prop], e)
  }

  render() {
    const { autoExpand, inputStyles, ...others } = this.props
    return (
      <TextField
        tag='textarea'
        onInput={(e) => this.onInput(e, 'onInput')}
        onKeyDown={(e) => this.onInput(e, 'onKeyDown')}
        inputStyles={{ resize: autoExpand ? 'none' : null, ...inputStyles }}
        {...others}
      />
    )
  }
}

